import 'package:flutter/material.dart';\nimport '../services/food_service.dart';\nimport '../services/location_service.dart';\nimport '../widgets/admin_food_tab.dart';\nimport '../widgets/admin_location_tab.dart';\n\nclass RestaurantAdminScreen extends StatefulWidget {\n  const RestaurantAdminScreen({super.key});\n\n  @override\n  State<RestaurantAdminScreen> createState() => _RestaurantAdminScreenState();\n}\n\nclass _RestaurantAdminScreenState extends State<RestaurantAdminScreen>\n    with SingleTickerProviderStateMixin {\n  late TabController _tabController;\n  final FoodService _foodService = FoodService();\n  final LocationService _locationService = LocationService();\n\n  @override\n  void initState() {\n    super.initState();\n    _tabController = TabController(length: 2, vsync: this);\n  }\n\n  @override\n  void dispose() {\n    _tabController.dispose();\n    super.dispose();\n  }\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text('Restaurant Admin'),\n        backgroundColor: Theme.of(context).colorScheme.primaryContainer,\n        bottom: TabBar(\n          controller: _tabController,\n          tabs: const [\n            Tab(icon: Icon(Icons.restaurant_menu), text: 'Products'),\n            Tab(icon: Icon(Icons.location_on), text: 'Locations'),\n          ],\n        ),\n      ),\n      body: TabBarView(\n        controller: _tabController,\n        children: [\n          AdminFoodTab(foodService: _foodService),\n          AdminLocationTab(locationService: _locationService),\n        ],\n      ),\n    );\n  }\n}\n