import 'package:flutter/foundation.dart';\nimport '../models/food_item.dart';\n\nclass FoodService extends ChangeNotifier {\n  final List<FoodItem> _foodItems = [\n    FoodItem(\n      id: '1',\n      name: 'Margherita Pizza',\n      description: 'Classic pizza with tomato, mozzarella, and basil',\n      price: 12.99,\n      category: 'Pizza',\n      imageUrl: 'https://via.placeholder.com/300x200?text=Margherita+Pizza',\n      restaurantId: 'rest1',\n    ),\n    FoodItem(\n      id: '2',\n      name: 'Cheeseburger',\n      description: 'Juicy beef patty with cheese, lettuce, and tomato',\n      price: 9.99,\n      category: 'Burgers',\n      imageUrl: 'https://via.placeholder.com/300x200?text=Cheeseburger',\n      restaurantId: 'rest1',\n    ),\n    FoodItem(\n      id: '3',\n      name: 'Caesar Salad',\n      description: 'Fresh romaine lettuce with Caesar dressing and croutons',\n      price: 7.99,\n      category: 'Salads',\n      imageUrl: 'https://via.placeholder.com/300x200?text=Caesar+Salad',\n      restaurantId: 'rest1',\n    ),\n    FoodItem(\n      id: '4',\n      name: 'Spaghetti Carbonara',\n      description: 'Creamy pasta with bacon and parmesan',\n      price: 13.99,\n      category: 'Pasta',\n      imageUrl: 'https://via.placeholder.com/300x200?text=Spaghetti+Carbonara',\n      restaurantId: 'rest1',\n    ),\n    FoodItem(\n      id: '5',\n      name: 'Chicken Wings',\n      description: 'Spicy buffalo wings with ranch dipping sauce',\n      price: 11.99,\n      category: 'Appetizers',\n      imageUrl: 'https://via.placeholder.com/300x200?text=Chicken+Wings',\n      restaurantId: 'rest1',\n    ),\n    FoodItem(\n      id: '6',\n      name: 'Fish Tacos',\n      description: 'Grilled fish with cabbage slaw and lime',\n      price: 10.99,\n      category: 'Mexican',\n      imageUrl: 'https://via.placeholder.com/300x200?text=Fish+Tacos',\n      restaurantId: 'rest1',\n    ),\n  ];\n\n  List<FoodItem> get foodItems => List.unmodifiable(_foodItems);\n\n  List<String> get categories {\n    return _foodItems.map((item) => item.category).toSet().toList()..sort();\n  }\n\n  List<FoodItem> getFoodItemsByCategory(String category) {\n    return _foodItems.where((item) => item.category == category && item.isAvailable).toList();\n  }\n\n  void addFoodItem(FoodItem item) {\n    _foodItems.add(item);\n    notifyListeners();\n  }\n\n  void updateFoodItem(FoodItem item) {\n    final index = _foodItems.indexWhere((i) => i.id == item.id);\n    if (index >= 0) {\n      _foodItems[index] = item;\n      notifyListeners();\n    }\n  }\n\n  void deleteFoodItem(String id) {\n    _foodItems.removeWhere((item) => item.id == id);\n    notifyListeners();\n  }\n\n  void toggleAvailability(String id) {\n    final index = _foodItems.indexWhere((item) => item.id == id);\n    if (index >= 0) {\n      _foodItems[index] = _foodItems[index].copyWith(\n        isAvailable: !_foodItems[index].isAvailable,\n      );\n      notifyListeners();\n    }\n  }\n}\n