import 'package:flutter/foundation.dart';\nimport '../models/location.dart';\n\nclass LocationService extends ChangeNotifier {\n  final List<DeliveryLocation> _locations = [\n    DeliveryLocation(\n      id: '1',\n      name: 'Downtown',\n      address: '123 Main St, Downtown',\n      latitude: 40.7128,\n      longitude: -74.0060,\n    ),\n    DeliveryLocation(\n      id: '2',\n      name: 'Uptown',\n      address: '456 Park Ave, Uptown',\n      latitude: 40.7829,\n      longitude: -73.9654,\n    ),\n    DeliveryLocation(\n      id: '3',\n      name: 'East Side',\n      address: '789 East Blvd, East Side',\n      latitude: 40.7489,\n      longitude: -73.9680,\n      isBlocked: true,\n      blockedReason: 'Temporarily unavailable due to construction',\n    ),\n  ];\n\n  List<DeliveryLocation> get locations => List.unmodifiable(_locations);\n\n  List<DeliveryLocation> get availableLocations {\n    return _locations.where((loc) => !loc.isBlocked).toList();\n  }\n\n  List<DeliveryLocation> get blockedLocations {\n    return _locations.where((loc) => loc.isBlocked).toList();\n  }\n\n  void addLocation(DeliveryLocation location) {\n    _locations.add(location);\n    notifyListeners();\n  }\n\n  void updateLocation(DeliveryLocation location) {\n    final index = _locations.indexWhere((loc) => loc.id == location.id);\n    if (index >= 0) {\n      _locations[index] = location;\n      notifyListeners();\n    }\n  }\n\n  void deleteLocation(String id) {\n    _locations.removeWhere((loc) => loc.id == id);\n    notifyListeners();\n  }\n\n  void blockLocation(String id, String reason) {\n    final index = _locations.indexWhere((loc) => loc.id == id);\n    if (index >= 0) {\n      _locations[index] = _locations[index].copyWith(\n        isBlocked: true,\n        blockedReason: reason,\n      );\n      notifyListeners();\n    }\n  }\n\n  void unblockLocation(String id) {\n    final index = _locations.indexWhere((loc) => loc.id == id);\n    if (index >= 0) {\n      _locations[index] = _locations[index].copyWith(\n        isBlocked: false,\n        blockedReason: null,\n      );\n      notifyListeners();\n    }\n  }\n}\n